// Basket.jsx
import React, { useContext, useEffect, useState } from 'react';
import { Tree } from '../Tree/Tree';
import c from './Basket.module.scss';
import { StateContext } from '../../context/context';
import { CartItem } from './CartItem';
import cl from 'classnames';

export const Basket = () => {
  const [products, setProducts] = useState([]);
  const { state } = useContext(StateContext);

  useEffect(() => {
    const orderProducts = state.order.map(orderItem => ({
      ...orderItem,
      amount: 1,  // Устанавливаем начальное количество для каждого товара
    }));
    setProducts(orderProducts);
  }, [state.order]);

  const updateAmount = (productId, newAmount) => {
    setProducts(prevProducts => prevProducts.map(product => 
      product.id === productId ? { ...product, amount: newAmount } : product
    ));
  };

  return (
    <div>
      <Tree title="Кошик" />
      <div className={c.table}>
        <div className={c.grid__row}>
          {/* ... заголовки столбцов ... */}
        </div>
        {products.map(product => (
          <CartItem
            key={product.id}
            product={product}
            updateAmount={updateAmount}
            products={products}
          />
        ))}
      </div>
      {/* Общая сумма и т.д. */}
    </div>
  );
};
