import React, { useContext, useEffect, useState } from "react";
import { Offers } from "../Offers/Offers";
import { StateContext } from "../../context/context";
import { useParams } from "react-router-dom";
import { Tree } from "../Tree/Tree";
import c from "./ViewPage.module.scss";
import { Swiper } from "swiper/react";
import { Navigation } from "swiper/modules";
import { SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";

export const ViewPage = (props) => {
  const [product, setProduct] = useState({});
  const { state } = useContext(StateContext);
  const { id } = useParams();
  useEffect(() => {
    const fetchedProduct = state.product.find((p) => p.id === parseInt(id));
    if (fetchedProduct) {
      setProduct(fetchedProduct);
    }
  }, [id, state.product]);
  console.log(product);
  return (
    <div>
      <Tree title={product.name} />
      <div className={c.view}>
        <div className={c.images}>
          {product.photo && <img src={product.photo.main} alt="" />}
          {product.photo && <img src={product.photo.display1} alt="" />}
          {product.photo && <img src={product.photo.display2} alt="" />}
          {product.photo && <img src={product.photo.display3} alt="" />}
        </div>
        {product.photo && (
            <div className={c.swiper__container}>
<Swiper navigation={true
                
            } loop={true} modules={[Navigation]} className={c.Swiper}>
              <SwiperSlide>
                <img src={product.photo.main} className={c.image} alt="" />
              </SwiperSlide>
              <SwiperSlide>
                <img src={product.photo.display1} className={c.image} alt="" />
              </SwiperSlide>
              <SwiperSlide>
                <img src={product.photo.display2} className={c.image} alt="" />
              </SwiperSlide>
              <SwiperSlide>
                <img src={product.photo.display3} className={c.image} alt="" />
              </SwiperSlide>
            </Swiper>
            </div>
          
        )}
      </div>

      <Offers product={state.product} type="new" title="Нові моделі" />
      <Offers product={state.product} type="popular" title="Популярні моделі" />
    </div>
  );
};
