import React, { useContext, useEffect, useState } from "react";
import { Tree } from "../Tree/Tree";
import c from "./Basket.module.scss";
import { StateContext } from "../../context/context";
import { CartItem } from "./CartItem";
import cl from "classnames";
export const Basket = () => {
  const [product, setProduct] = useState([]);

  // const [amounts, setAmounts] = useState([]);
  const { state1 } = useContext(StateContext);
  useEffect(() => {
    const product = state1.order;
    if (product) {
      setProduct(product);
    }
  }, [state1.order]);

  return (
    <div>
      <Tree title="Кошик" />
      <div className={c.table}>
        <div className={c.grid__row}>
          <span className={cl(c.grid__el, c.grid__title_1)}>&nbsp;</span>
          <span className={cl(c.grid__el, c.grid__title_1)}>Назва товару</span>
          <span className={cl(c.grid__el, c.grid__title_1)}>Кількість</span>
          <span className={cl(c.grid__el, c.grid__title_1)}>Ціна</span>
          <span className={cl(c.grid__el, c.grid__title_1)}>Разом</span>
          <span className={cl(c.grid__el, c.grid__title_1)}>&nbsp;</span>
        </div>
        {product.map((or) => {
          return (
            <CartItem
              key={or.id}
              product={or}
              setProduct={setProduct}
              products={product}
            />
          );
        })}
      </div>
      <div className={c.result_container}>
        <div className={c.result}>
          Разом:{" "}
          {state1.order.reduce((total, order) => total + order.amount, 0)}{" "}
          товара за{" "}
          <span className={c.total}>
            {state1.order.reduce((total, order) => total + order.total, 0)} ₴
          </span>
        </div>
      </div>
      <div className={c.checkout}>
        <button className={c.checkout__button}>Оформлення заказу</button>
      </div>
    </div>
  );
};
