import React, {  useState} from 'react'
import c from './Basket.module.scss'
import cl from 'classnames'
export const CartItem = ({ product, productIndex, amount, onAmountChange }) => {
    const handleIncrement = () => {
        onAmountChange(amount + 1, productIndex);
      };
      const handleDecrement = () => {
        if (amount > 1) {
          onAmountChange(amount - 1, productIndex);
        }
      };
    const [amout,setAmount]=useState(1);
  return (<>
    <div className={c.grid__row}>
            <div className={c.grid__el} >{product.photo &&<img src={product.photo.main} className={cl(c.grid__img)}/>}</div>
            <span className={cl(c.grid__el,c.grid__title_2)}>{product.name}</span>
            <div className={cl(c.grid__el,c.grid__title_2)}>
            <div className={cl(c.grid__buttons,c.grid__el)}>
            <button  className={c.button__plus} onClick={handleIncrement()} >+</button>
            <span className={c.grid__amount}>{amout}</span>
            <button className={c.button__minus} onClick={handleDecrement()}>-</button>
            </div>
            </div>
            <span className={cl(c.grid__el,c.grid__title_2)}>{product.sale} ₴</span>
            <span className={cl(c.grid__el,c.grid__title_2)}>{product.sale * amout} ₴</span>
            
            </div>
                
            </>
  )
}
