import React, { useContext, useEffect, useState } from 'react'
import { Tree } from '../Tree/Tree'
import c from './Basket.module.scss'
import { StateContext } from '../../context/context';
import { CartItem } from './CartItem';
import cl from 'classnames';
export const Basket = () => {
    const [product, setProduct] = useState([]);
  const { state } = useContext(StateContext);
  useEffect(() => {
    const product = state.product;
    if(product){
      setProduct(product);

    }
      
    },[state.product])
    
  return (
    <div>
        <Tree title="Кошик"/>
        <div className={c.grid}>
            <span className={cl(c.grid__header, c.grid__title_1)}>&nbsp;</span>
            <span className={cl(c.grid__header, c.grid__title_1)}>Назва товару</span>
            <span className={cl(c.grid__header, c.grid__title_1)}>Кількість</span>
            <span className={cl(c.grid__header, c.grid__title_1)}>Ціна</span>
            <span className={cl(c.grid__header, c.grid__title_1)}>Разом</span>
            <span className={cl(c.grid__header, c.grid__title_1)}>&nbsp;</span>
            
            <div className={cl(c.grid__el,c.grid__img)} >{state.product[3].photo &&<img src={state.product[3].photo.main}/>}</div>
            <span className={cl(c.grid__el,c.grid__title_2)}>{state.product[3].name}</span>
            <div className={cl(c.grid__el,c.grid__title_2)}>
            <button >+</button>
            1
            <button >-</button>
            </div>
            <span className={cl(c.grid__el,c.grid__title_2)}>{state.product[3].sale} ₴</span>
            <span className={cl(c.grid__el,c.grid__title_2)}>{state.product[3].sale } ₴</span>
            
            
        </div>
        <table class="cart__item__table">
            <tr>
            <td>&nbsp;</td>
            <td>Назва товару</td>
            <td>Кількість</td>
            <td>Ціна</td>
            <td>Разом</td>
            </tr>
            
            <CartItem product={state.product[3]}/>
            <CartItem product={state.product[3]}/>
            
      </table>
    </div>
  )
}
