import React, { useEffect, useState } from "react";
import { ModelItem } from "../ModelItem/ModelItem";
import c from "./Catalogs.module.css";
import { Swiper, SwiperSlide } from "swiper/react";

// import required modules
import { Grid, Navigation } from "swiper/modules";
// Import Swiper styles
import "swiper/css";

import "./../../App.css";
import { v1 } from "uuid";

export const Catalogs = (props) => {
  const [prev,setPrev]=useState()
  const [next,setNext]=useState()
  let product = props.product.filter((p) => {
    return p.type.some((t) => t === props.type);
  });
  useEffect(() => {
    setPrev(v1())
    setNext(v1())
  }, []);
  return (
    <div className={c.wrapper}>
      <div className={c.main}>
        <div className={c.title}>{props.title}</div>
        <div className={c.buttons}>
          <div className={`${c.prev}_${prev}`}></div>
          <div className={`${c.next}_${next}`}></div>
        </div>
      </div>

      <Swiper
        spaceBetween={32}
        slidesPerView={4} 
        navigation={{
          prevEl: `.${c.prev}`,
          nextEl: `.${c.next}`,
        }}
        modules={[Grid, Navigation]}
        className="mySwiper"
      >
        {product.map((p) => {
          return (
            <SwiperSlide>
              <ModelItem
                key={p.id}
                name={p.name}
                title={p.title}
                id={p.id}
                sale={p.sale}
                cost={p.cost}
              />
            </SwiperSlide>
          );
        })}
      </Swiper>
    </div>
  );
};
