import React from "react";
import { ModelItem } from "../ModelItem/ModelItem";
import c from "./Catalogs.module.css";
import { Swiper, SwiperSlide } from 'swiper/react';

// import required modules
import { Grid, Navigation, Pagination } from 'swiper/modules';
// Import Swiper styles


import './../../App.css';

export const Catalogs = (props) => {
  let product = props.product.filter((p) => {
    return p.type.some((t) => t === props.type);
  });

  return (
    <div className={c.wrapper}>
      <div className={c.window}>
      <div className={c.title}>
        {props.title} 
      </div>
      
      <Swiper
        spaceBetween={10}
        slidesPerView={3}
        
        pagination={{
          clickable: true,
        }}
        breakpoints={{
          640: {
            slidesPerView: 2,
            spaceBetween: 20,
          },
          768: {
            slidesPerView: 4,
            spaceBetween: 40,
          },
          1024: {
            slidesPerView: 5,
            spaceBetween: 50,
          },
        }}modules={[Grid,Navigation,Pagination]}
        className="mySwiper"
      >
        {product.map((p) => {
          return (
            <SwiperSlide>
              <ModelItem key={p.id}
              name={p.name}
              title={p.title}
              id={p.id}
              sale={p.sale}
              cost={p.cost}
            />
            </SwiperSlide>
            
          );})}
      </Swiper>
        
        
        
        
      </div>
    </div>
  );
};
