import  { createContext, useEffect, useState } from 'react';
import { state } from '../data';
export const fakeGetData = () => {
    return new Promise((resolve) => {
      setTimeout(() => {
        resolve(state);
      }, 1);
    });
}

export const StateContext=createContext({
    state:{
    },
    addOrder:()=>{
      }
})
export const addOrder=(product1)=>{
    if(product1){
    state.order=[...state.order,{img:product1.photo.main,price:product1.sale,id:product1.id,name:product1.name,
    title:product1.title,amount:1}]
    console.log(state.order)
    }
  }
export function StateProvider(props){
    const [state,setState]=useState(state)
    const addOrder = (product1) => {
    if (product1) {
      setState((prev) => ({
        ...prev,
        order: [...prev.order, { img: product1.photo.main, price: product1.sale, id: product1.id, name: product1.name, title: product1.title, amount: 1 }],
      }));
    }}
    useEffect(()=>{
        setState(state)
    },[])

return(
    <StateContext.Provider value={{state,addOrder}}>
        {props.children}
    </StateContext.Provider>
)
}
