import React, { useContext } from 'react';
import { StateContext } from '../../../context/context';
import c from './Order.module.scss';
import { useNavigate } from 'react-router-dom';
export const Order = () => {
  const navigate = useNavigate();
  const [product, setProduct] = useState([]);

  // const [amounts, setAmounts] = useState([]);
  const { state1 } = useContext(StateContext);
  useEffect(() => {
    const product = state1.order;
    if (product) {
      setProduct(product);
    }
  }, [state1.order]);
  return (
    <>
      <div className={c.wrapper}>
        <div className={c.order__container}>
          <h2 className={c.order__title}>Оформлення заказу</h2>
          <div className={c.order__content}>
            <Form />

            <div className={c.order__view}>
              <p className={c.view__title}>Ваше замовлення</p>
              <p className={c.view__subtitle}>Продавець: БМ Shop</p>
              {state1.order.map(or => (
                <ProductBlock key={or.id} product={or} remove={removeOrder} />
              ))}
              <div className={c.view__params}>
                <a
                  href=""
                  className={c.view__link}
                  onClick={() => {
                    navigate('/basket');
                  }}
                >
                  Редагувати товари
                </a>
                <div className={c.view__count}>
                  <p>
                    {state1.order.reduce(
                      (total, order) => total + order.amount,
                      0,
                    )}{' '}
                    товар на суму:
                  </p>
                  <p>
                    {state1.order.reduce(
                      (total, order) => total + order.total,
                      0,
                    )}{' '}
                    ₴
                  </p>
                </div>
                <div className={c.view__count}>
                  <p>Вартість доставки:</p>
                  <p>за тарифами перевізника</p>
                </div>
                <div className={c.view__count}>
                  <p>Промокоди</p>
                  <label className={c.view__add}>
                    <button className={c.add__button}>+</button>Додати
                  </label>
                </div>
                <div className={c.view__payment}>
                  <p>До сплати</p>
                  <p className={c.view__sum}>
                    {state1.order.reduce(
                      (total, order) => total + order.total,
                      0,
                    )}{' '}
                    ₴
                  </p>
                </div>
              </div>
            </div>
          </div>

          <div></div>
        </div>
      </div>
    </>
  );
};
const Input = ({ text, clas = '' }) => {
  return (
    <>
      <input
        type="text"
        placeholder={text}
        className={`${c.order__input} ${clas}`}
      />
    </>
  );
};
const Form = () => {
  return (
    <div className={c.order__form}>
      <div className={`${c.form__contact} ${c.form__element}`}>
        <p className={c.element__title}>Контактна інформація</p>
        <div className={c.contact__inputs}>
          <Input text={"Ім'я"} />
          <Input text={'Призвище'} />
          <Input text={'+ 38 (0__)___-__-__'} />
          <Input text={'Електронна форма'} />
        </div>
      </div>
      <div className={`${c.form__address} ${c.form__element}`}>
        <div className={c.element__title}>Адреса доставки</div>
        <div className={c.element__location}>
          <div>
            <div className={`${c.address__icon} ${c.element__icon}`}></div>
            <span className={c.element__subtitle}>
              Дніпро. Дніпропетровьска обл. 49045
            </span>
          </div>
          <div className={c.element__change}>
            <a href="">Змінити</a>
          </div>
        </div>
      </div>
      <div className={`${c.form__payment} ${c.form__element}`}>
        <div className={c.element__title}>Оплата</div>
        <div className={c.element__location}>
          <div>
            <div className={`${c.payment_icon} ${c.element__icon}`}></div>
            <span className={c.element__subtitle}>
              Оплата під час отримання товару
            </span>
          </div>
          <div className={c.element__change}>
            <a href="">Змінити</a>
          </div>
        </div>
      </div>
      <div className={`${c.form__comment} ${c.form__element}`}>
        <p className={c.comment__title}>Ваш коментар до замовлення</p>
        <Input clas={c.comment__input} text={'Ваш коментар'} />
      </div>
    </div>
  );
};
const ProductBlock = ({ product, remove }) => {
  const removeOrderPage = id => {
    remove(id);
  };
  return (
    <>
      <div className={c.product}>
        {product.img && <img src={product.img} className={c.product__image} />}
        <div>
          {product.amount > 1 && <span>{product.amount}x </span>}
          <span className={c.product__name}>{product.name}</span>
          <span> - {product.price}₴</span>
        </div>
        <button
          className={c.product__button}
          onClick={removeOrderPage(product.id)}
        ></button>
      </div>
    </>
  );
};
