import { createContext, useEffect, useState } from 'react';
import { state } from '../data';

export const StateContext = createContext({
  state: {
    product: {},
    order: [],
  },
  addOrder: () => {},
});

export const addOrder = (product1, setState) => {
  if (product1) {
    setState((prev) => ({
      ...prev,
      order: [
        ...prev.order,
        {
          img: product1.photo.main,
          price: product1.sale,
          id: product1.id,
          name: product1.name,
          title: product1.title,
          amount: 1,
          total: product1.price,
        },
      ],
    }));
  }
};

export const updateOrder = (id, plus, setState) => {
  setState((prev) => ({
    ...prev,
    order: prev.order.map((item) => {
      if (item.id === id) {
        if (plus) {
          item.amount = item.amount + 1;
          item.total = item.amount * item.price;
        } else if (item.amount > 1) {
          item.amount = item.amount - 1;
          item.total = item.amount * item.price;
        }
      }
      return item;
    }),
  }));
};

export const removeOrder = (id, setState) => {
  setState((prev) => ({
    ...prev,
    order: prev.order.filter((or) => or.id !== id),
  }));
};

export function StateProvider(props) {
  const [state1, setState] = useState(state);

  useEffect(() => {
    setState(state);
  }, []);

  return (
    <StateContext.Provider value={{ state1, addOrder: (product1) => addOrder(product1, setState) }}>
      {props.children}
    </StateContext.Provider>
  );
}
