import { BrowserRouter, Navigate, Route, Routes } from 'react-router-dom';
import './App.css';
import { Footer } from './components/Footer/Footer';
import { Header } from './components/Header/Header';
import { MainPage } from './components/MainPage/MainPage';
import {state} from './data.js'
import { AboutUs } from './components/AboutUs/AboutUs.jsx';
import { ViewPage } from './components/ViewPage/ViewPage.jsx';
import { StateContext,addOrder } from './context/context.jsx';
import { Basket } from './components/Basket/Basket.jsx';

function App() {

  return (
    <StateContext.Provider value={{ state , addOrder}}>
    <BrowserRouter>
    <div className="App">
      <div className='header'>
        <Header/>
      </div>
      <div className='content'>

        <Routes>
        <Route path="/*"   element={
             <Navigate to="/main" />}/>
        <Route path="/main/:id"  element={<ViewPage />}/>
          <Route path="/main"  element={<MainPage />}/>
          <Route path='/about' element={<AboutUs state={state}/>}/>
          <Route path='/basket' element={<Basket state={state}/>}/>
        </Routes>

      </div>
      <footer className='footer'>
        <Footer /> 
      </footer>
    </div>
    </BrowserRouter>
    </StateContext.Provider>
  );
}

export default App;
