const fs = require('fs');
const path = require('path');

const imagesPath = './img/productImage';
const imageExtensions = ['.jpg', '.jpeg', '.png', '.gif'];

const getImages = () => {
  const imageFiles = fs.readdirSync(imagesPath).filter(file => {
    const filePath = path.join(imagesPath, file);
    const stats = fs.statSync(filePath);
    return stats.isFile() && imageExtensions.includes(path.extname(filePath));
  });

  const images = {};
  imageFiles.forEach(file => {
    const fileName = path.basename(file, path.extname(file));
    images[fileName] = require(`./img/productImage/${file}`).default;
  });

  return images;
};

