  const addOrder = (product1) => {
    if (product1) {
      setState((prev) => ({
        ...prev,
        order: [
          ...prev.order,
          {
            img: product1.photo.main,
            price: product1.sale,
            id: product1.id,
            name: product1.name,
            title: product1.title,
            amount: 1,
            total: product1.price
          },
        ],
      }));
    }
  };

  const updateOrder = (id, plus) => {
    setState((prev) => ({
      ...prev,
      order: prev.order.map((item) => {
        if (item.id === id) {
          if (plus) {
            item.amount = item.amount + 1;
            item.total = item.amount * item.price;
          } else if (item.amount > 1) {
            item.amount = item.amount - 1;
            item.total = item.amount * item.price;
          }
        }
        return item;
      }),
    }));
  };

  const removeOrder = (id) => {
    setState((prev) => ({
      ...prev,
      order: prev.order.filter((or) => or.id !== id),
    }));
  };

  useEffect(() => {
    setState(state);
  }, []);

  return (
    <StateContext.Provider value={{ state1, addOrder, updateOrder, removeOrder }}>
      {props.children}
    </StateContext.Provider>
  );
};
