import React, { useContext, useEffect, useState } from 'react'
import { Tree } from '../Tree/Tree'
import c from './Basket.module.scss'
import { StateContext } from '../../context/context';
import { CartItem } from './CartItem';
import cl from 'classnames';
export const Basket = () => {
    const [product, setProduct] = useState([]);
    
    // const [amounts, setAmounts] = useState([]);
  const { state } = useContext(StateContext);
//   const handleAmount=(amount)=>{
//     const amounts2 = [...amounts]
//   }
  useEffect(() => {
    const product = state.product;
    if(product){
      setProduct(product);

    }
      
    },[state.product])
    
  return (
    <div>
        <Tree title="Кошик"/>
        <div className={c.table}>
            <div className={c.grid__row}>
            <span className={cl(c.grid__el,c.grid__title_1)}>&nbsp;</span>
            <span className={ cl(c.grid__el,c.grid__title_1)}>Назва товару</span>
            <span className={ cl(c.grid__el,c.grid__title_1)}>Кількість</span>
            <span className={ cl(c.grid__el,c.grid__title_1)}>Ціна</span>
            <span className={ cl(c.grid__el,c.grid__title_1)}>Разом</span>
            <span className={ cl(c.grid__el,c.grid__title_1)}>&nbsp;</span>
            </div>
            
            <CartItem product={state.product[3]}/>
            <CartItem product={state.product[3]}/>
            
            
        </div>
        <span>Разом:  товара за {} ₴</span>
        
    </div>
  )
}
