import React from "react";
// Import Swiper React components
import { Swiper, SwiperSlide, useSwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";

import "./App.css";
import { Navigation, EffectCoverflow, A11y, Pagination } from "swiper";

export const SlideComp = ({ index }) => {
  const swiperSlide = useSwiperSlide();
  console.log(index, swiperSlide.isVisible);
  return <div>slide {index}</div>;
};
export default function App() {
  return (
    <>
      <Swiper
        loop={true}
        modules={[Navigation, EffectCoverflow, A11y, Pagination]}
        keyboard={false}
        centeredSlides
        navigation
        effect={"slide"}
        slideToClickedSlide
        pagination={{
          clickable: true
        }}
        slidesPerGroup={1}
        spaceBetween={10}
        slidesPerView={1.53}
        coverflowEffect={{
          rotate: 0,
          stretch: 80,
          depth: 200,
          modifier: 1,
          slideShadows: false
        }}
      >
        <SwiperSlide>
          <SlideComp index={1} />
        </SwiperSlide>
        <SwiperSlide>
          <SlideComp index={2} />
        </SwiperSlide>
        <SwiperSlide>
          <SlideComp index={3} />
        </SwiperSlide>
        <SwiperSlide>
          <SlideComp index={4} />
        </SwiperSlide>
      </Swiper>
    </>
  );
}
