import React, { useContext, useEffect, useState } from 'react'
import { Tree } from '../Tree/Tree'
import './Basket.scss';
import { StateContext } from '../../context/context';
export const Basket = () => {
    const [product, setProduct] = useState([]);
    const []=useState()
  const { state } = useContext(StateContext);
  useEffect(() => {
    const product = state.product;
    if(product){
      setProduct(product);
    }
      
    },[state.product])
  return (
    <div>
        <Tree title="Кошик"/>
        <table class="cart__item__table">
            <tr>
            <td>&nbsp;</td>
            <td>Назва товару</td>
            <td>Кількість</td>
            <td>Ціна</td>
            <td>Разом</td>
            </tr>
            <tr>

            <td>{product[3].photo &&<img src={product[3].photo.main}/>}</td>
            <td>{product[3].name}</td>
            <td>Кількість</td>
            <td>{product[3].sale} ₴</td>
            <td>{product[3].sale} ₴</td>
            </tr>
      </table>
    </div>
  )
}
