import React, { useContext, useEffect, useState } from 'react'
import { Tree } from '../Tree/Tree'
import './Basket.scss';
import { StateContext } from '../../context/context';
export const Basket = () => {
    const [product, setProduct] = useState([]);
    const [amout,setAmount]=useState(1)
  const { state } = useContext(StateContext);
  useEffect(() => {
    const product = state.product;
    if(product){
      setProduct(product);

    }
      
    },[state.product])
    const incrementAmount = () => {
        setAmount(amout + 1);
      }
      const decrementAmount = () => {
        if (amout > 1) {
          setAmount(amout - 1);
        }
      }
  return (
    <div>
        <Tree title="Кошик"/>
        <table class="cart__item__table">
            <tr>
            <td>&nbsp;</td>
            <td>Назва товару</td>
            <td>Кількість</td>
            <td>Ціна</td>
            <td>Разом</td>
            </tr>
            <tr>

            <td>{state.product[3].photo &&<img src={state.product[3].photo.main}/>}</td>
            <td>{state.product[3].name}</td>
            <td>
            <button onClick={()=>{setAmount(amout+1)}}>+</button>
            {amout}
            <button onClick={decrementAmount}>-</button>
            </td>
            <td>{state.product[3].sale} ₴</td>
            <td>{state.product[3].sale * amout} ₴</td>
            </tr>
      </table>
    </div>
  )
}
