import React, { useState } from 'react'
import c from './TabBar.module.scss'
export const TabBar = () => {
    const [toogle,setToogle]=useState(1);
  return (
    <div>
        <button className={toogle===1 ?c.active :c.none_active} onClick={()=>{setToogle(1)}}>Опис</button>
        <button className={toogle===2 ?c.active :c.none_active} onClick={()=>{setToogle(2)}}>Характеристики</button>
        <button className={toogle===3 ?c.active :c.none_active} onClick={()=>{setToogle(3)}}>Доставка і оплата</button>

         {toogle===1&&<div>TEXTEXTTEXXTXTE</div>}
         
         {toogle===2&&<div>CHARACHTERS</div>}
         {toogle===3&&<div>Delivery</div>}
    </div>
  )
}
