import React, { useState } from 'react'
import c from './Basket.module.scss'
export const CartItem = ({product}) => {
    
    const [amout,setAmount]=useState(1);
  return (
    <div className={c.cart__item}>
    <tr  >
            
            <td>{product.photo &&<img src={product.photo.main}/>}</td>
            <td>{product.name}</td>
            <td >
            <button onClick={()=>{setAmount(amout+1)}}>+</button>
            {amout}
            <button onClick={()=>{
                    if(amout>1){setAmount(amout-1)}}}>-</button>
            </td>
            <td>{product.sale} ₴</td>
            <td>{product.sale * amout} ₴</td>
            
            
            </tr>
            </div>
  )
}
