import React, {  useContext, useEffect, useState} from 'react'
import c from './Basket.module.scss'
import cl from 'classnames'
import { StateContext } from '../../context/context';
export const CartItem = ({product,setProduct,products,updateAmount}) => {
  const handleAmountChange = newAmount => {
    setAmount(newAmount);
    updateAmount(product.id, newAmount);
  };
    const [amout,setAmount]=useState(1);
    const {removeOrder,updateOrder}=useContext(StateContext)

    function removeOrderPage(id){
        const stateFiltred =products.filter(or=>{
            
            return or.id !== id;
          })
        setProduct(stateFiltred)
        removeOrder(id)
    }
    
  return (<>
    <div className={c.grid__row}>
            <div className={c.grid__el} >{product.img &&<img src={product.img} className={cl(c.grid__img)}/>}</div>
            <span className={cl(c.grid__el,c.grid__title_2)}>{product.name}</span>
            <div className={cl(c.grid__el,c.grid__title_2)}>
            <div className={cl(c.grid__buttons,c.grid__el)}>
            <button  className={c.button__plus} onClick={()=>{setAmount(amout+1)}} >+</button>
            <span className={c.grid__amount}>{amout}</span>
            <button className={c.button__minus} onClick={()=>{
                    if(amout>1){setAmount(amout-1)}}}>-</button>
            </div>
            </div>
            <span className={cl(c.grid__el,c.grid__title_2)}>{product.price} ₴</span>
            <span className={cl(c.grid__el,c.grid__title_2)}>{product.price * amout} ₴</span>
            <button className={c.button__minus} onClick={()=>{removeOrderPage(product.id )}}>X</button>
            </div>
                
            </>
  )
}
