import React, { useContext } from 'react';
import { StateContext } from '../../../context/context';
import c from './Order.module.scss';
export const Order = () => {
  const { state1 } = useContext(StateContext);

  return (
    <>
      <div className={c.wrapper}>
        <div className={c.order__container}>
          <h2 className={c.order__title}>Оформлення заказу</h2>
          <div className={c.order__form}>
            <div className={`${c.form__contact} ${c.form__element}`}>
              <span className={c.contact__title}>Контактна інформація</span>
              <div className={c.contact__inputs}>
                <Input text={"Ім'я"} />
                <Input text={'Призвище'} />
                <Input text={'+ 38 (0__)___-__-__'} />
                <Input text={'Електронна форма'} />
              </div>
            </div>
            <div className={`${c.form__address} ${c.form__element}`}>d</div>
            <div className={`${c.form__payment} ${c.form__element}`}>в</div>
            <div className={`${c.form__comment} ${c.form__element}`}>в</div>
          </div>
          <div className={c.order__view}></div>
          <div></div>
        </div>
      </div>
    </>
  );
};
const Input = ({ text }) => {
  return (
    <>
      <input type="text" placeholder={text} className={c.order__input} />
    </>
  );
};
