import  { createContext, useEffect, useState } from 'react';
import { state } from '../data';
export const fakeGetData = () => {
    return new Promise((resolve) => {
      setTimeout(() => {
        resolve(state);
      }, 1);
    });
}

export const StateContext=createContext({
    state:{
        product:{},
        order:{}
    },
    addOrder:()=>{
      }
})
export const addOrder=(product1)=>{
    if(product1){
    state.order=[...state.order,{img:product1.photo.main,price:product1.sale,id:product1.id,name:product1.name,
    title:product1.title,amount:1,total:product1.price}]
    console.log(state.order)
    }
  }
  export const updateOrder=(id,plus)=>{
    const updatedOrder = state.order.map((item) => {
      if (item.id === id) {
        if (plus) {
          item.amount = item.amount + 1;
          item.total = item.amount * item.price;
        } else {
          if (item.amount > 1) {
            item.amount = item.amount - 1;
            item.total = item.amount * item.price;
          }
        }
      }
      return item;
    });
    state.order=updatedOrder;
    console.log(state.order)
  }
  export const removeOrder=(id)=>{
    state.order=state.order.filter(or=>{
            
      return or.id !== id;})
  }
