import React, { useContext, useEffect, useState } from 'react';
import { Offers } from '../../utils/Offers/Offers';
import { StateContext } from '../../../context/context';
import { Tree } from '../../utils/Tree/Tree';
import c from './CatalogPage.module.scss';
import ReactSlider from 'react-slider';
import { ModelItem } from '../../utils/ModelItem/ModelItem';

export const CatalogPage = () => {
  const { state1 } = useContext(StateContext);
  const [selectedFilters, setSelectedFilters] = useState([]);
  const [sortedProducts, setSortedProducts] = useState([...state1.product]);
  const [finalProduct, setFinalProduct] = useState([...sortedProducts]);
  const sortByPrice = () => {
    const sorted = [...state1.product].sort((p1, p2) => p1.sale - p2.sale);
    setSortedProducts(sorted);
  };
  const sortByPopular = () => {
    const sorted = [...state1.product].sort((p1, p2) => p2.bought - p1.bought);
    setSortedProducts(sorted);
  };
  function filteredData(products, selected, query) {
    let filteredProducts = products;

    // Filtering Input Items
    if (query) {
      filteredProducts = filteredItems;
    }

    // Applying selected filter
    if (selected) {
      filteredProducts = filteredProducts.filter(
        ({ category, color, company, newPrice, title }) =>
          category === selected ||
          color === selected ||
          company === selected ||
          newPrice === selected ||
          title === selected,
      );
    }

    return filteredProducts.map(
      ({ img, title, star, reviews, prevPrice, newPrice }) => (
        <Card
          key={Math.random()}
          img={img}
          title={title}
          star={star}
          reviews={reviews}
          prevPrice={prevPrice}
          newPrice={newPrice}
        />
      ),
    );
  }
  return (
    <div>
      <Tree title="Всі" />
      <div className={c.catalogPage__content}>
        <div className={c.content__container}>
          <Filter sortTemplate={sortTemplate} />
          <div className={c.content}>
            <MiniFilter
              sortByPrice={sortByPrice}
              sortByPopular={sortByPopular}
            />
            <div className={c.product__sorting}>
              <div className={c.products}>
                {finalProduct.filter().map(p => {
                  return (
                    <ModelItem
                      key={p.id}
                      name={p.name}
                      title={p.title}
                      id={p.id}
                      sale={p.sale}
                      cost={p.cost}
                      photo={p.photo.main}
                    />
                  );
                })}
              </div>
              <Paginator />
            </div>
          </div>
        </div>
      </div>
      <Offers product={state1.product} type="new" title="Нові моделі" />
      <Offers
        product={state1.product}
        type="popular"
        title="Популярні моделі"
      />
    </div>
  );
};
const Checkbox = ({ title, count }) => {
  return (
    <div className={c.checkbox}>
      <label className={c.checkbox_label}>
        <input type="checkbox" className={c.checkbox__item} />
        <span>{count ? `${title}(${count})` : title}</span>
      </label>
    </div>
  );
};
const DRS = ({ min, max, reverse, sortTemplate, type }) => {
  const [values, setValue] = useState([min, max]);

  const handleFirstInputChange = e => {
    const newValue = parseInt(e.target.value, 10);
    setValue([Math.min(newValue, values[1] - 1), values[1]]);
    sortTemplate('range', values, `${type}`);
  };

  const handleSecondInputChange = e => {
    const newValue = parseInt(e.target.value, 10);
    setValue([values[0], Math.max(newValue, values[0] + 1)]);
    sortTemplate('range', values, `${type}`);
  };
  return (
    <div>
      {!reverse ? (
        <div className={c.drs}>
          <div className={c.drs__values}>
            <div className={c.drs__from}>
              <div>від</div>
              <div>
                <input
                  className={c.drs__input}
                  type="number"
                  value={values[0]}
                  onChange={e => {
                    setValue([parseInt(e.target.value, 10), values[1]]);
                  }}
                  onBlur={handleFirstInputChange}
                />
              </div>
            </div>
            <div className={c.drs__upto}>
              <div>до</div>
              <div>
                <input
                  className={c.drs__input}
                  type="number"
                  value={values[1]}
                  onChange={e => {
                    setValue([values[0], parseInt(e.target.value, 10)]);
                  }}
                  onBlur={handleSecondInputChange}
                />
              </div>
            </div>
          </div>

          <div>
            <ReactSlider
              className={c.horizontal_slider}
              thumbClassName={c.example_thumb}
              trackClassName={c.example_track}
              value={values}
              min={min}
              max={max}
              onChange={setValue}
              onAfterChange={data => sortTemplate('range', values, `${type}`)}
              minDistance={1}
            />
          </div>
        </div>
      ) : (
        <div className={c.drs__reverse}>
          <div>
            <ReactSlider
              className={c.horizontal_slider}
              thumbClassName={c.example_thumb}
              trackClassName={c.example_track}
              value={values}
              min={min}
              max={max}
              onChange={setValue}
              onAfterChange={data => sortTemplate('range', values, `${type}`)}
              minDistance={1}
            />
          </div>
          <div className={c.drs__values}>
            <div className={c.drs__from}>
              <div>від</div>
              <div>
                <input
                  className={c.drs__input}
                  type="number"
                  value={values[0]}
                  onChange={e => {
                    setValue([parseInt(e.target.value, 10), values[1]]);
                  }}
                  onBlur={handleFirstInputChange}
                />
              </div>
            </div>
            <div className={c.drs__upto}>
              <div>до</div>
              <div>
                <input
                  className={c.drs__input}
                  type="number"
                  value={values[1]}
                  onChange={e => {
                    setValue([values[0], parseInt(e.target.value, 10)]);
                  }}
                  onBlur={handleSecondInputChange}
                />
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
const Filter = ({ sortTemplate }) => {
  return (
    <div className={c.filter}>
      <div className={`${c.filter__element} ${c.filter__DRS}`}>
        <div className={c.filter__title}>Ціна(грн)</div>
        <DRS
          min={6000}
          max={50000}
          reverse={false}
          sortTemplate={sortTemplate}
          type={'sale'}
        />
      </div>
      <div className={c.filter__element}>
        <div className={c.filter__title}>Жорсткість матраса</div>
        <div className={c.filter__checkboxs}>
          <Checkbox title={"1-й рівень, м'який"} count={'49'} />
          <Checkbox title={"2-й рівень, середнєм'який"} count={'67'} />
          <Checkbox title={'3-й рівень, середній'} count={'78'} />
          <Checkbox title={'4-й рівень, жорсткий'} count={'45'} />
        </div>
      </div>
      <div className={c.filter__element}>
        <div className={c.filter__title}>Трансформація</div>
        <div className={c.filter__checkboxs}>
          <Checkbox title={'Не розкладається'} />
          <Checkbox title={'Єврокнижка'} />
          <Checkbox title={'Акордеон'} />
          <Checkbox title={'Розкладачка'} />
          <Checkbox title={'Дельфін'} />
          <Checkbox title={'Французька розкладачка'} />
          <Checkbox title={'Седафлекс'} />
          <Checkbox title={'Алеко'} />
          <Checkbox title={'Крокуюча єврокнижка'} />
        </div>
      </div>
      <div className={c.filter__element}>
        <div className={c.filter__title}>Конфігурація</div>
        <div className={c.filter__checkboxs}>
          <Checkbox title={'Односпальні'} />
          <Checkbox title={'Полуторні'} />
          <Checkbox title={'Двоспальні'} />
          <Checkbox title={'Одномісні'} />
          <Checkbox title={'Двухмісні'} />
          <Checkbox title={'Тримісні'} />
          <Checkbox title={'Багатомісні'} />
        </div>
      </div>
      <div className={c.filter__element}>
        <div className={c.filter__title}>Підлокітники</div>
        <div className={c.filter__checkboxs}>
          <Checkbox title={"Дерев'яні"} count={'21'} />
          <Checkbox title={'Модульні ДСП'} count={'34'} />
          <Checkbox title={'Модульні МДФ'} count={'2'} />
          <Checkbox title={'Тканинні'} count={'34'} />
          <Checkbox title={'Без підлокітників'} />
        </div>
      </div>
      <div className={c.filter__element}>
        <div className={c.filter__title}>Розмір спального місця</div>
        <div className={c.drs__block}>
          <div className={c.drs__item}>
            <div className={c.drs__title}>Ширина, см</div>
            <DRS
              min={500}
              max={3000}
              reverse={true}
              sortTemplate={sortTemplate}
              type={'width'}
            />
          </div>
          <div className={c.drs__item}>
            <div className={c.drs__title}>Довжина, см</div>
            <DRS
              min={500}
              max={3000}
              reverse={true}
              sortTemplate={sortTemplate}
              type={'length'}
            />
          </div>
        </div>
      </div>
    </div>
  );
};
const MiniFilter = ({ sortByPrice, sortByPopular }) => {
  const [IsSortPrice, setIsSortPrice] = useState(false);
  const [IsSortPopular, setIsSortPopular] = useState(true);
  const handleSortPrice = () => {
    setIsSortPopular(false);
    setIsSortPrice(true);
    sortByPrice();
  };
  const handleSortPopular = () => {
    setIsSortPrice(false);
    setIsSortPopular(true);
    sortByPopular();
  };
  return (
    <div className={c.mini__filter}>
      <div className={c.mini__title}>Сортувати по:</div>
      <FilterButton
        text="ціні"
        active={IsSortPrice}
        setSort={handleSortPrice}
      />
      <FilterButton
        text="популярності"
        active={IsSortPopular}
        setSort={handleSortPopular}
      />
    </div>
  );
};
const FilterButton = ({ text, active, setSort }) => {
  return (
    <div onClick={setSort}>
      <button className={!active ? c.mini__button : c.mini__buttonActive}>
        {text}
      </button>
    </div>
  );
};
const Paginator = ({
  // totalCount,
  // pageSize,
  // currentPage,
  // changePage,
  portionSize = 6,
}) => {
  // let pageCount = Math.ceil(totalCount/pageSize)
  const pageCount = 20;
  const page = [];
  for (let i = 1; i <= pageCount; i++) {
    page.push(i);
  }

  const portionCount = Math.ceil(pageCount / portionSize);
  const [portionNumber, setPortionNumber] = useState(1);
  const leftPortionNumber = (portionNumber - 1) * portionSize + 1;
  const rightPortionNumber = portionNumber * portionSize;
  return (
    <div className={c.paginator}>
      <div
        className={c.button__container}
        onClick={() => {
          if (portionNumber > 1) {
            setPortionNumber(prev => prev - 1);
          }
        }}
      >
        {portionNumber > 1 ? (
          <button className={c.paginator__prev}></button>
        ) : (
          <button className={`${c.paginator__prev} ${c.disable}`}></button>
        )}
      </div>
      <div className={c.paginator__pages}>
        {portionNumber > 1 && (
          <span
            onClick={() => {
              setPortionNumber(1);
            }}
            className={c.paginator__dots}
          >
            ..
          </span>
        )}
        {page
          .filter(p => p >= leftPortionNumber && p <= rightPortionNumber)
          .map(i => (
            <span className={c.paginator__number} onClick={() => {}} key={i}>
              {i}
            </span>
          ))}
        {portionNumber < portionCount && (
          <div
            onClick={() => {
              setPortionNumber(portionCount);
            }}
            className={c.paginator__dots}
          >
            ..
          </div>
        )}
      </div>
      <div
        className={c.button__container}
        onClick={() => {
          if (portionNumber < portionCount) {
            setPortionNumber(prev => prev + 1);
          }
        }}
      >
        {portionNumber < portionCount ? (
          <button className={c.paginator__next}></button>
        ) : (
          <button className={`${c.paginator__next} ${c.disable}`}></button>
        )}
      </div>
    </div>
  );
};
