  const handleSortPrice = () => {
    setIsSortPopular(false);
    setIsSortPrice(true);
    sortByPrice();
  };
  const handleSortPopular = () => {
    setIsSortPrice(false);
    setIsSortPopular(true);
    sortByPopular();
  };
  const handleSortChange = event => {
    const value = event.target.value;
    if (value === 'cost') {
      setIsSortPrice(true);
      setIsSortPopular(false);
      sortByPrice();
    } else if (value === 'popular') {
      setIsSortPrice(false);
      setIsSortPopular(true);
      sortByPopular();
