// Basket.jsx
import React, { useContext, useEffect, useState } from 'react';
import { Tree } from '../Tree/Tree';
import c from './Basket.module.scss';
import { StateContext } from '../../context/context';
import { CartItem } from './CartItem';
import cl from 'classnames';

export const Basket = () => {
  const [product, setProduct] = useState([]);
  const { state } = useContext(StateContext);

  useEffect(() => {
    const orderProducts = state.order.map(orderItem => ({
      ...orderItem,
      amount: 1,  // Устанавливаем начальное количество для каждого товара
    }));
    setProduct(orderProducts);
  }, [state.order]);

  const updateAmount = (productId, newAmount) => {
    setProduct(prevProducts => prevProducts.map(product => 
      product.id === productId ? { ...product, amount: newAmount } : product
    ));
  };

  return (
    
    <div>
        <Tree title="Кошик"/>
        <div className={c.table}>
            <div className={c.grid__row}>
            <span className={cl(c.grid__el,c.grid__title_1)}>&nbsp;</span>
            <span className={ cl(c.grid__el,c.grid__title_1)}>Назва товару</span>
            <span className={ cl(c.grid__el,c.grid__title_1)}>Кількість</span>
            <span className={ cl(c.grid__el,c.grid__title_1)}>Ціна</span>
            <span className={ cl(c.grid__el,c.grid__title_1)}>Разом</span>
            <span className={ cl(c.grid__el,c.grid__title_1)}>&nbsp;</span>
            </div>
            {product.map(or=>{
                return <CartItem updateAmount={updateAmount} product={or} setProduct={setProduct} products={product}/>
            })}
            
            
            
        </div>
        <span>Разом:  товара за {} ₴</span>
        
    </div>
  )
    
}
